import numpy as np

def selectValues(array, trigger=0):
    values = []
    idx=0
    for element in array:
        idx = idx + 1
        if element>trigger:
            values.append(idx)
    return values
def division(array, total):
    div=[]
    for element in array:
        div.append(float(element)/total)
    return div
def suma(array):
    sum=0
    for element in array:
        sum+=element
    return sum
def coincidencia(array):
    mcoincidencia = []
    for i in range(43):
        conteo=0
        for element in array:
            if((i+1)==element):
                conteo +=1
        mcoincidencia.append(conteo)
    return mcoincidencia

